// Generated by CoffeeScript 1.3.3
(function() {
  var socket;

  socket = io.connect(window.location.hostname);

  socket.on('connect', function() {
    return socket.emit('adduser', $('#username').val());
  });

  socket.on('updatechat', function(username, data) {
    return $('#conversation').append('<b>' + username + ':</b> ' + data + '<br>');
  });

  socket.on('updateusers', function(data) {
    $('#users').empty();
    return $.each(data, function(key, value) {
      return $('#users').append('<div>' + key + '</div>');
    });
  });

  $(function() {
    $('#datasend').click(function() {
      var message;
      message = $('#data').val();
      $('#data').val('');
      return socket.emit('sendchat', message);
    });
    return $('#data').keypress(function(e) {
      if (e.which === 13) {
        $(this).blur();
        return $('#datasend').focus().click();
      }
    });
  });

}).call(this);
